import React, { useState } from "react";
import { TextField, Button, Typography, Box } from "@mui/material";

const FuturesTaxCalculator: React.FC = () => {
  const [netProfit, setNetProfit] = useState<number>(0);
  const [taxSlabRate, setTaxSlabRate] = useState<number>(30); // Default: 30%
  const [tax, setTax] = useState<number | null>(null);

  const calculateTax = () => {
    const taxLiability = netProfit * (taxSlabRate / 100) + netProfit * 0.04;
    setTax(taxLiability);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h5" gutterBottom>
        Futures Trading Tax Calculator
      </Typography>
      <TextField
        label="Net Profit (₹)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mb: 2 }}
        onChange={(e) => setNetProfit(Number(e.target.value))}
      />
      <TextField
        label="Tax Slab Rate (%)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mb: 2 }}
        onChange={(e) => setTaxSlabRate(Number(e.target.value))}
      />
      <Button variant="contained" color="primary" onClick={calculateTax}>
        Calculate Tax
      </Button>
      {tax !== null && (
        <Typography variant="h6" sx={{ mt: 3 }}>
          Tax Payable: ₹{tax.toFixed(2)}
        </Typography>
      )}
    </Box>
  );
};

export default FuturesTaxCalculator;
