import React, { useState } from 'react';
import { calculateSpotTax } from './TaxUtils';

const SpotTaxCalculator: React.FC = () => {
  const [sellingPrice, setSellingPrice] = useState<number>(0);
  const [purchasePrice, setPurchasePrice] = useState<number>(0);
  const [tdsPaid, setTdsPaid] = useState<number>(0);
  const [result, setResult] = useState<any>(null);

  const handleCalculate = () => {
    const res = calculateSpotTax(sellingPrice, purchasePrice, tdsPaid);
    setResult(res);
  };

  return (
    <div>
      <h2>Spot Trading Tax Calculator</h2>
      <input
        type="number"
        placeholder="Selling Price"
        onChange={(e) => setSellingPrice(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Purchase Price"
        onChange={(e) => setPurchasePrice(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="TDS Paid"
        onChange={(e) => setTdsPaid(Number(e.target.value))}
      />
      <button onClick={handleCalculate}>Calculate</button>

      {result && (
        <div>
          <p>Tax Liability: ₹{result.taxLiability.toFixed(2)}</p>
          <p>Surcharge: ₹{result.surcharge.toFixed(2)}</p>
          <p>Cess: ₹{result.cess.toFixed(2)}</p>
          <p>Total Tax Payable: ₹{result.taxPayable.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default SpotTaxCalculator;
