import React, { useState } from "react";
import { TextField, Button, Typography, Box, Grid, Checkbox, FormControlLabel } from "@mui/material";
import { useNavigate } from "react-router-dom";

interface SpotTransaction {
  sellingPrice: number;
  purchasePrice: number;
  profit: number;
}

const SpotTaxCalculator: React.FC = () => {
  const [transactions, setTransactions] = useState<SpotTransaction[]>([]);
  const [sellingPrice, setSellingPrice] = useState<number | null>(null);
  const [purchasePrice, setPurchasePrice] = useState<number | null>(null);
  const [tdsDeducted, setTdsDeducted] = useState<number>(0);
  const [includeCess, setIncludeCess] = useState<boolean>(true);
  const [totalTax, setTotalTax] = useState<number | null>(null);
  const navigate = useNavigate();

  const addTransaction = () => {
    if (sellingPrice !== null && purchasePrice !== null) {
      const profit = sellingPrice - purchasePrice;
      const newTransaction: SpotTransaction = {
        sellingPrice,
        purchasePrice,
        profit,
      };
      setTransactions([...transactions, newTransaction]);
      setSellingPrice(null);
      setPurchasePrice(null);
    }
  };

  const calculateTax = () => {
    const totalProfit = transactions.reduce((sum, txn) => sum + txn.profit, 0);
    const taxLiability = totalProfit * 0.3 + (includeCess ? totalProfit * 0.04 : 0);
    const finalTax = taxLiability - tdsDeducted;
    setTotalTax(finalTax);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Button
        variant="outlined"
        color="secondary"
        onClick={() => navigate("/")}
        sx={{ mb: 2 }}
      >
        Back
      </Button>
      <Typography variant="h5" gutterBottom>
        Spot Trading Tax Calculator
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <TextField
            label="Selling Price (₹)"
            type="number"
            variant="outlined"
            fullWidth
            value={sellingPrice || ""}
            onChange={(e) => setSellingPrice(Number(e.target.value))}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Purchase Price (₹)"
            type="number"
            variant="outlined"
            fullWidth
            value={purchasePrice || ""}
            onChange={(e) => setPurchasePrice(Number(e.target.value))}
          />
        </Grid>
      </Grid>
      <Button
        variant="contained"
        color="primary"
        onClick={addTransaction}
        sx={{ mt: 2 }}
      >
        Add Transaction
      </Button>
      <Box sx={{ mt: 3 }}>
        <Typography variant="h6">Transactions:</Typography>
        {transactions.map((txn, idx) => (
          <Typography key={idx}>
            Transaction {idx + 1}: Selling Price: ₹{txn.sellingPrice}, Purchase
            Price: ₹{txn.purchasePrice}, Profit: ₹{txn.profit.toFixed(2)}
          </Typography>
        ))}
      </Box>
      <TextField
        label="TDS Deducted (₹)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mt: 2 }}
        onChange={(e) => setTdsDeducted(Number(e.target.value))}
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={includeCess}
            onChange={(e) => setIncludeCess(e.target.checked)}
          />
        }
        label="Include Cess (4%)"
        sx={{ mt: 2 }}
      />
      <Button variant="contained" color="primary" onClick={calculateTax} sx={{ mt: 2 }}>
        Calculate Total Tax
      </Button>
      {totalTax !== null && (
        <Typography variant="h6" sx={{ mt: 3 }}>
          Total Tax Payable: ₹{totalTax.toFixed(2)}
        </Typography>
      )}
    </Box>
  );
};

export default SpotTaxCalculator;
