import React, { useState } from 'react';
import { Button, TextField, Typography, Container, Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';
import { calculateSpotTaxForTransactions } from './TaxUtils';

interface Transaction {
  sellingPrice: number;
  purchasePrice: number;
  tdsPaid: number;
}

const SpotTaxCalculator: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [result, setResult] = useState<any>(null);

  const addTransaction = () => {
    setTransactions([...transactions, { sellingPrice: 0, purchasePrice: 0, tdsPaid: 0 }]);
  };

  const handleTransactionChange = (index: number, field: keyof Transaction, value: number) => {
    const updatedTransactions = [...transactions];
    updatedTransactions[index][field] = value;
    setTransactions(updatedTransactions);
  };

  const handleCalculate = () => {
    const res = calculateSpotTaxForTransactions(transactions);
    setResult(res);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Spot Trading Tax Calculator
      </Typography>
      <Button variant="contained" onClick={addTransaction} style={{ marginBottom: '20px' }}>
        Add Transaction
      </Button>
      {transactions.length > 0 && (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Selling Price</TableCell>
              <TableCell>Purchase Price</TableCell>
              <TableCell>TDS Paid</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {transactions.map((transaction, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    type="number"
                    value={transaction.sellingPrice}
                    onChange={(e) => handleTransactionChange(index, 'sellingPrice', parseFloat(e.target.value))}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    value={transaction.purchasePrice}
                    onChange={(e) => handleTransactionChange(index, 'purchasePrice', parseFloat(e.target.value))}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    value={transaction.tdsPaid}
                    onChange={(e) => handleTransactionChange(index, 'tdsPaid', parseFloat(e.target.value))}
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
      <Button variant="contained" color="primary" onClick={handleCalculate} style={{ marginTop: '20px' }}>
        Calculate
      </Button>

      {result && (
        <div style={{ marginTop: '20px' }}>
          <Typography>Total Profit: ₹{result.totalProfit.toFixed(2)}</Typography>
          <Typography>Tax Liability: ₹{result.taxLiability.toFixed(2)}</Typography>
          <Typography>Surcharge: ₹{result.surcharge.toFixed(2)}</Typography>
          <Typography>Cess: ₹{result.cess.toFixed(2)}</Typography>
          <Typography>Total Tax Payable: ₹{result.taxPayable.toFixed(2)}</Typography>
        </div>
      )}
    </Container>
  );
};

export default SpotTaxCalculator;
