import React, { useState } from "react";
import { TextField, Button, Typography, Box, Grid } from "@mui/material";

interface FuturesTransaction {
  sellingPrice: number;
  purchasePrice: number;
  fees: number;
  profit: number;
}

const FuturesTaxCalculator: React.FC = () => {
  const [transactions, setTransactions] = useState<FuturesTransaction[]>([]);
  const [sellingPrice, setSellingPrice] = useState<number | null>(null);
  const [purchasePrice, setPurchasePrice] = useState<number | null>(null);
  const [fees, setFees] = useState<number>(0);
  const [totalTax, setTotalTax] = useState<number | null>(null);
  const [taxSlabRate, setTaxSlabRate] = useState<number>(30);

  const addTransaction = () => {
    if (sellingPrice !== null && purchasePrice !== null) {
      const profit = sellingPrice - purchasePrice - fees;
      const newTransaction: FuturesTransaction = {
        sellingPrice,
        purchasePrice,
        fees,
        profit,
      };
      setTransactions([...transactions, newTransaction]);
      setSellingPrice(null);
      setPurchasePrice(null);
      setFees(0);
    }
  };

  const calculateTax = () => {
    const totalProfit = transactions.reduce((sum, txn) => sum + txn.profit, 0);
    const taxLiability =
      totalProfit * (taxSlabRate / 100) + totalProfit * 0.04;
    setTotalTax(taxLiability);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h5" gutterBottom>
        Futures Trading Tax Calculator
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={4}>
          <TextField
            label="Selling Price (₹)"
            type="number"
            variant="outlined"
            fullWidth
            value={sellingPrice || ""}
            onChange={(e) => setSellingPrice(Number(e.target.value))}
          />
        </Grid>
        <Grid item xs={4}>
          <TextField
            label="Purchase Price (₹)"
            type="number"
            variant="outlined"
            fullWidth
            value={purchasePrice || ""}
            onChange={(e) => setPurchasePrice(Number(e.target.value))}
          />
        </Grid>
        <Grid item xs={4}>
          <TextField
            label="Fees (₹)"
            type="number"
            variant="outlined"
            fullWidth
            value={fees}
            onChange={(e) => setFees(Number(e.target.value))}
          />
        </Grid>
      </Grid>
      <Button
        variant="contained"
        color="primary"
        onClick={addTransaction}
        sx={{ mt: 2 }}
      >
        Add Transaction
      </Button>
      <Box sx={{ mt: 3 }}>
        <Typography variant="h6">Transactions:</Typography>
        {transactions.map((txn, idx) => (
          <Typography key={idx}>
            Transaction {idx + 1}: Selling Price: ₹{txn.sellingPrice}, Purchase
            Price: ₹{txn.purchasePrice}, Fees: ₹{txn.fees}, Profit: ₹
            {txn.profit.toFixed(2)}
          </Typography>
        ))}
      </Box>
      <TextField
        label="Tax Slab Rate (%)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mt: 2 }}
        value={taxSlabRate}
        onChange={(e) => setTaxSlabRate(Number(e.target.value))}
      />
      <Button variant="contained" color="primary" onClick={calculateTax} sx={{ mt: 2 }}>
        Calculate Total Tax
      </Button>
      {totalTax !== null && (
        <Typography variant="h6" sx={{ mt: 3 }}>
          Total Tax Payable: ₹{totalTax.toFixed(2)}
        </Typography>
      )}
    </Box>
  );
};

export default FuturesTaxCalculator;
