import React, { useState } from "react";

const AirdropTaxCalculator: React.FC = () => {
  const [fairMarketValue, setFairMarketValue] = useState<number | "">("");
  const [quantity, setQuantity] = useState<number | "">("");
  const [taxRate, setTaxRate] = useState<number>(30); // Default rate
  const [tax, setTax] = useState<number | null>(null);

  const calculateAirdropTax = () => {
    if (fairMarketValue && quantity) {
      const totalValue = fairMarketValue * quantity;
      const taxAmount = totalValue * (taxRate / 100);
      setTax(taxAmount);
    }
  };

  return (
    <div>
      <h2>Airdrop Tax Calculator</h2>
      <label>
        Fair Market Value (per token):
        <input
          type="number"
          value={fairMarketValue}
          onChange={(e) => setFairMarketValue(Number(e.target.value))}
        />
      </label>
      <label>
        Quantity:
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
        />
      </label>
      <button onClick={calculateAirdropTax}>Calculate Tax</button>
      {tax !== null && <p>Airdrop Tax: â‚¹{tax.toFixed(2)}</p>}
    </div>
  );
};

export default AirdropTaxCalculator;
