import React, { useState } from "react";
import { Box, Button, Typography } from "@mui/material";
import * as XLSX from "xlsx";
import TransactionsTable from "./TransactionsTable";

interface Transaction {
  date: string;
  market: string;
  type: string;
  price: number;
  amount: number;
  total: number;
}



const UploadTransactions: React.FC = () => {
  const [groupedTransactions, setGroupedTransactions] = useState<{
    [key: string]: Transaction[];
  }>({});

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const binaryStr = event.target?.result;
      const workbook = XLSX.read(binaryStr, { type: "binary" });
      const sheetName = workbook.SheetNames[0];
      const sheetData: Transaction[] = XLSX.utils.sheet_to_json(
        workbook.Sheets[sheetName]
      );
      processTransactions(sheetData);
    };
    reader.readAsBinaryString(file);
  };

  const processTransactions = (data: Transaction[]) => { // Extract the first row as headers
    const [headers, ...rows] = jsonData;

    if (!headers || headers.length === 0) {
      console.error("Invalid file format: Missing headers");
      return [];
    }

    // Format headers: Capitalize and remove extra spaces
    const formattedHeaders = headers.map((header: any) => {
      if (typeof header === "string") {
        return header.trim().charAt(0).toUpperCase() + header.trim().slice(1);
      }
      return header;
    });

    // Map rows to objects using headers
    const transactions = rows.map((row) => {
      const transaction: Record<string, any> = {};
      formattedHeaders.forEach((header, index) => {
        transaction[header] = row[index] || "";
      });
      return transaction;
    });

    return transactions;
  };

  const handleEditTransaction = (
    coin: string,
    index: number,
    field: keyof Transaction,
    value: number
  ) => {
    const updatedTransactions = { ...groupedTransactions };
    if (updatedTransactions[coin]) {
      // Type assertion ensures TypeScript understands the type relationship
      (updatedTransactions[coin][index][field] as number) = value;
    }
    setGroupedTransactions(updatedTransactions);
  };


  const handleDeleteTransaction = (coin: string, index: number) => {
    const updatedTransactions = { ...groupedTransactions };
    if (updatedTransactions[coin]) {
      updatedTransactions[coin] = updatedTransactions[coin].filter(
        (_, i) => i !== index
      );
    }
    setGroupedTransactions(updatedTransactions);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h5" gutterBottom>
        Upload Transactions
      </Typography>
      <Box sx={{ mb: 3 }}>
        <Button variant="contained" component="label" color="primary">
          Upload Transactions File
          <input
            type="file"
            hidden
            accept=".xlsx, .xls"
            onChange={handleFileUpload}
          />
        </Button>
      </Box>
      <TransactionsTable
        groupedTransactions={groupedTransactions}
        onEditTransaction={handleEditTransaction}
        onDeleteTransaction={handleDeleteTransaction}
      />
    </Box>
  );
};

export default UploadTransactions;
