import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Button, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Box, IconButton, TextField, Grid } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

type EditedTransactionType = {
  Quantity: number;
  Total: number;
  [key: string]: string | number; // For any additional fields that might exist
};


// Helper function to calculate tax on profit
const calculateTax = (purchasePrice: number, sellPrice: number, quantity: number) => {
  const profit = (sellPrice - purchasePrice) * quantity;
  const tax = profit * 0.30; // 30% tax rate on profit
  return tax;
};

const UploadTransactions = () => {
  const [transactions, setTransactions] = useState<any[]>([]);
  const [isEditing, setIsEditing] = useState<boolean>(false);
  const [editedTransaction, setEditedTransaction] = useState<EditedTransactionType>({
    Quantity: 0,
    Total: 0,
  });
  // Handle file upload
  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    if (!file) {
      console.error("No file selected");
      return;
    }

    const reader = new FileReader();

    reader.onload = (e) => {
      const binaryStr = e.target?.result;
      if (typeof binaryStr === 'string') {
        const workbook = XLSX.read(binaryStr, { type: 'binary' });
        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];
        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 }) as any[][];

        const transactions = processExcelData(jsonData);
        setTransactions(transactions);
      }
    };

    reader.readAsBinaryString(file);
  };

  // Process Excel data and return transaction objects
  const processExcelData = (jsonData: any[][]): any[] => {
    const [headers, ...rows] = jsonData;
    const formattedHeaders = headers.map((header: any) => header.trim().charAt(0).toUpperCase() + header.trim().slice(1));

    return rows.map((row) => {
      const transaction: Record<string, any> = {};
      formattedHeaders.forEach((header, index) => {
        transaction[header] = row[index] || '';
      });
      return transaction;
    });
  };

  // Edit transaction
  const handleEdit = (transaction: any) => {
    setIsEditing(true);
    setEditedTransaction({ ...transaction });
  };

  // Save the edited transaction
  const handleSaveEdit = () => {
    const updatedTransactions = [...transactions];
    const index = updatedTransactions.findIndex((t) => t.Date === editedTransaction?.Date && t.Market === editedTransaction?.Market);
    if (index !== -1) {
      updatedTransactions[index] = editedTransaction!;
      setTransactions(updatedTransactions);
      setIsEditing(false);
      setEditedTransaction(null);
    }
  };

  // Handle changes to editable fields
  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>,
    field: string
  ) => {
    const { value } = e.target;

    setEditedTransaction((prev) => ({
      ...prev,
      [field]: value,
    }));
  };


  // Handle delete transaction
  const handleDelete = (transaction: any) => {
    const updatedTransactions = transactions.filter((t) => t.Date !== transaction.Date || t.Market !== transaction.Market);
    setTransactions(updatedTransactions);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom align="center">
        Upload Transactions
      </Typography>

      <Box sx={{ textAlign: 'center', marginBottom: 3 }}>
        <Button
          variant="contained"
          component="label"
          color="primary"
          sx={{ padding: '10px 20px', fontSize: '16px' }}
        >
          Choose File
          <input
            type="file"
            accept=".xlsx, .xls"
            hidden
            onChange={handleFileUpload}
          />
        </Button>
      </Box>

      {/* Display Table if there are transactions */}
      {transactions.length > 0 && (
        <Box sx={{ marginTop: 4 }}>
          <Typography variant="h5" gutterBottom align="center">
            Uploaded Transactions
          </Typography>

          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="transaction table">
              <TableHead>
                <TableRow>
                  {Object.keys(transactions[0]).map((key) => (
                    <TableCell key={key} align="center">{key}</TableCell>
                  ))}
                  <TableCell align="center">Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {transactions.map((transaction, idx) => {
                  // Calculate profit and tax for each transaction
                  const tax = calculateTax(transaction.Price, transaction.Total, transaction.Quantity);
                  return (
                    <TableRow key={idx}>
                     {Object.entries(transaction).map(([key, value], index) => {
  // Safely type-cast value to string or number
  let displayValue: React.ReactNode = String(value);

  if (key === 'Type') {
    displayValue =
      value === 'Sell' ? (
        <span style={{ color: 'red', textTransform: 'uppercase' }}>SELL</span>
      ) : value === 'Buy' ? (
        <span style={{ color: 'green', textTransform: 'uppercase' }}>BUY</span>
      ) : (
        String(value)
      );
  }

  return (
    <TableCell key={index} align="center">
      {displayValue}
    </TableCell>
  );
})}

                      <TableCell align="center">
                        <IconButton onClick={() => handleEdit(transaction)}>
                          <EditIcon />
                        </IconButton>
                        <IconButton onClick={() => handleDelete(transaction)}>
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </TableContainer>

          {/* Edit Transaction Dialog */}
          {isEditing && editedTransaction && (
            <Box sx={{ marginTop: 2 }}>
              <Typography variant="h6" gutterBottom>
                Edit Transaction
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <TextField
                    label="Quantity"
                    type="number"
                    value={editedTransaction.Quantity}
                    onChange={(e) => handleChange(e, 'Quantity')}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    label="Total"
                    type="number"
                    value={editedTransaction.Total}
                    onChange={(e) => handleChange(e, 'Total')}
                    fullWidth
                  />
                </Grid>
              </Grid>
              <Button variant="contained" color="primary" onClick={handleSaveEdit} sx={{ marginTop: 2 }}>
                Save Changes
              </Button>
            </Box>
          )}
        </Box>
      )}
    </Box>
  );
};

export default UploadTransactions;
