import React, { useState } from "react";
import { TextField, Button, Typography, Box } from "@mui/material";

const SpotTaxCalculator: React.FC = () => {
  const [sellingPrice, setSellingPrice] = useState<number>(0);
  const [purchasePrice, setPurchasePrice] = useState<number>(0);
  const [tdsDeducted, setTdsDeducted] = useState<number>(0);
  const [tax, setTax] = useState<number | null>(null);

  const calculateTax = () => {
    const profit = sellingPrice - purchasePrice;
    const taxLiability = profit * 0.3 + profit * 0.04;
    const finalTax = taxLiability - tdsDeducted;
    setTax(finalTax);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h5" gutterBottom>
        Spot Trading Tax Calculator
      </Typography>
      <TextField
        label="Selling Price (₹)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mb: 2 }}
        onChange={(e) => setSellingPrice(Number(e.target.value))}
      />
      <TextField
        label="Purchase Price (₹)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mb: 2 }}
        onChange={(e) => setPurchasePrice(Number(e.target.value))}
      />
      <TextField
        label="TDS Deducted (₹)"
        type="number"
        variant="outlined"
        fullWidth
        sx={{ mb: 2 }}
        onChange={(e) => setTdsDeducted(Number(e.target.value))}
      />
      <Button variant="contained" color="primary" onClick={calculateTax}>
        Calculate Tax
      </Button>
      {tax !== null && (
        <Typography variant="h6" sx={{ mt: 3 }}>
          Tax Payable: ₹{tax.toFixed(2)}
        </Typography>
      )}
    </Box>
  );
};

export default SpotTaxCalculator;
