import React, { useState } from 'react';
import { calculateFuturesTax } from './TaxUtils';

const FuturesTaxCalculator: React.FC = () => {
  const [sellingPrice, setSellingPrice] = useState<number>(0);
  const [purchasePrice, setPurchasePrice] = useState<number>(0);
  const [fees, setFees] = useState<number>(0);
  const [expenses, setExpenses] = useState<number>(0);
  const [taxRate, setTaxRate] = useState<number>(0);
  const [result, setResult] = useState<any>(null);

  const handleCalculate = () => {
    const res = calculateFuturesTax(
      sellingPrice,
      purchasePrice,
      fees,
      expenses,
      taxRate
    );
    setResult(res);
  };

  return (
    <div>
      <h2>Futures Trading Tax Calculator</h2>
      <input
        type="number"
        placeholder="Selling Price"
        onChange={(e) => setSellingPrice(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Purchase Price"
        onChange={(e) => setPurchasePrice(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Fees"
        onChange={(e) => setFees(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Other Expenses"
        onChange={(e) => setExpenses(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Tax Rate (%)"
        onChange={(e) => setTaxRate(Number(e.target.value))}
      />
      <button onClick={handleCalculate}>Calculate</button>

      {result && (
        <div>
          <p>Net Profit: ₹{result.netProfit.toFixed(2)}</p>
          <p>Tax Liability: ₹{result.taxLiability.toFixed(2)}</p>
          <p>Cess: ₹{result.cess.toFixed(2)}</p>
          <p>Total Tax: ₹{result.totalTax.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default FuturesTaxCalculator;
