import React, { useState } from 'react';
import Homepage from './components/Homepage';
import SpotTaxCalculator from './components/SpotTaxCalculator';
import FuturesTaxCalculator from './components/FuturesTaxCalculator';

const App: React.FC = () => {
  const [view, setView] = useState<'home' | 'spot' | 'futures'>('home');

  const renderView = () => {
    switch (view) {
      case 'spot':
        return <SpotTaxCalculator />;
      case 'futures':
        return <FuturesTaxCalculator />;
      default:
        return <Homepage onSelect={setView} />;
    }
  };

  return <div>{renderView()}</div>;
};

export default App;
